mapper.select:
/* getListUserInterestTotalCount */
SELECT COUNT(user_interest_id)
FROM user_interest


        user_id = #{userId}


        AND urban_id = #{urbanId}


        AND urban_order = #{urbanOrder}


        AND ${searchWord} = #{searchValue}


        AND ${searchWord} LIKE '%' || #{searchValue} || '%'



            AND insert_date >= TO_TIMESTAMP(#{startDate}, 'YYYYMMDDHH24MISSUS')




            AND insert_date <= TO_TIMESTAMP(#{endDate}, 'YYYYMMDDHH24MISSUS')

/* getListUserInterest */
SELECT *
FROM user_interest


        user_id = #{userId}


        AND urban_id = #{urbanId}


        AND urban_order = #{urbanOrder}


        AND ${searchWord} = #{searchValue}


        AND ${searchWord} LIKE '%' || #{searchValue} || '%'



            AND insert_date >= TO_TIMESTAMP(#{startDate}, 'YYYYMMDDHH24MISSUS')




            AND insert_date <= TO_TIMESTAMP(#{endDate}, 'YYYYMMDDHH24MISSUS')





        ORDER BY ${orderWord} ${orderValue}


        ORDER BY insert_date DESC, user_interest_id DESC


OFFSET #{offset} LIMIT #{limit}

/* getUserInterest */
SELECT *
FROM user_interest


        AND user_id = #{userId}


        AND urban_id = #{urbanId}


        AND urban_order = #{urbanOrder}

mapper.insert:
/* insertUserInterest */

    SELECT NEXTVAL('user_interest_seq')

INSERT INTO user_interest (
    user_interest_id,

        contents,

    urban_id, urban_order, user_id
) VALUES (
    #{userInterestId},

        #{contents}::jsonb,

    #{urbanId}, #{urbanOrder}, #{userId}
)

mapper.update:
/* updateUserInterest */
UPDATE user_interest
SET

    contents = #{contents}::jsonb,

update_date = NOW()
WHERE user_interest_id = #{userInterestId}

    AND user_id = #{userId}


    AND urban_id = #{urbanId}


    AND urban_order = #{urbanOrder}

mapper.delete:
/* deleteUserInterest */
DELETE
FROM user_interest
WHERE user_interest_id = #{userInterestId}

    AND user_id = #{userId}


    AND urban_id = #{urbanId}


    AND urban_order = #{urbanOrder}
